import { useState, useEffect } from 'react';
import api from '../services/api';

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    async function getCategories() {
      try {
        const response = await api.get('/categories');
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    }

    getCategories();
  }, []);

  let expenses = categories.filter(category => category.type === 'expense');
  let earnings = categories.filter(category => category.type === 'earning');

  expenses = expenses.map(expense => {
    delete expense.id;
    delete expense.type;
  });

  earnings = expenses.map(expense => {
    delete expense.id;
    delete expense.type;
  });
  return [expenses, earnings];
};

export default useCategories;
