import React, { useState, useEffect, useContext } from 'react';
import { FaCheck, FaTimes } from 'react-icons/fa';
import PageTitle from '../../components/PageTitle';
import Card from '../../containers/Card';
import api from '../../services/api';
import {
  TransactionItem,
  TransactionStatus,
  TransactionTitle,
  TransactionValue,
  TransactionDate,
  TransactionsTotals,
  BalanceColumn,
  BalanceColumnData,
  StyledToggleFaPlus,
} from './styles';
import { Button } from '../../components/Button';
import Modal from '../../containers/Modal';
import AddTransaction from '../../components/AddTransaction';
import { TransactionsContext } from '../../contexts/TransactionContext';
import totals from '../../backend/totals';

const Transactions = () => {
  const [transactions, setTransactions] = useContext(TransactionsContext);
  const [modalOpen, setModalOpen] = useState(false);
  const [showBalanceDetails, setShowBalanceDetails] = useState(false);

  const {
    income,
    predictedIncome,
    expenses,
    predictedExpenses,
    balance,
    predictedBalance,
  } = totals(transactions);

  useEffect(() => {
    async function getTransactions() {
      try {
        const response = await api.get('/transactions');
        setTransactions(response.data);
      } catch (error) {
        console.error(error);
      }
    }

    getTransactions();
  }, []);

  const handleClose = () => {
    setModalOpen(false);
  };

  return (
    <>
      <PageTitle>Transactions</PageTitle>
      <Card backgroundColor="#fff" borderRadius="10px">
        <ul>
          {transactions.map(transaction => {
            return (
              <TransactionItem key={transaction.id}>
                <TransactionDate>
                  {new Date(transaction.date).toLocaleDateString()}
                </TransactionDate>
                <TransactionTitle>{transaction.title}</TransactionTitle>
                <TransactionStatus status={transaction.paid}>
                  {transaction.paid ? <FaCheck /> : <FaTimes />}
                </TransactionStatus>
                <TransactionValue>{transaction.amount}</TransactionValue>
              </TransactionItem>
            );
          })}
        </ul>
        <TransactionsTotals as="div">
          <div>
            <h3>Balance</h3>
          </div>
          <BalanceColumn>
            <BalanceColumnData>
              {showBalanceDetails && (
                <>
                  <span>
                    <span>Income: </span>
                    {income}
                  </span>
                  <span>
                    <span>Predicted income: </span>
                    {predictedIncome}
                  </span>
                  <span>
                    <span>Expenses</span>
                    {predictedExpenses}
                  </span>
                </>
              )}
              <span>
                <span>Balance: </span>
                {balance}
              </span>
              <span>
                <span>Predicted balance: </span>
                {predictedBalance}
              </span>
              <span>
                <span>Balance: </span>
                {balance}
              </span>
            </BalanceColumnData>
            <StyledToggleFaPlus
              onClick={() => {
                setShowBalanceDetails(true);
              }}
            />
          </BalanceColumn>
        </TransactionsTotals>
      </Card>
      <Button onClick={() => setModalOpen(true)}>Add Transaction</Button>
      <Modal open={modalOpen} onClose={handleClose}>
        <AddTransaction modalClose={handleClose} />
      </Modal>
    </>
  );
};

export default Transactions;
