import styled from 'styled-components';
import { FaMinus, FaPlus } from 'react-icons/fa';
import { MyRadioButton } from '../FormComponents';

const StyledFaMinus = styled(FaMinus)`
  margin-right: 5px;
`;

const StyledFaPlus = styled(FaPlus)`
  margin-right: 5px;
`;

const MyTransactionTypeButton = styled(MyRadioButton)`
  & input {
    display: none;
  }
`;

export { StyledFaMinus, StyledFaPlus };
