import { useState, useEffect } from 'react';
import api from '../services/api';

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    async function getCategories() {
      try {
        const response = await api.get('/categories');
        console.log(response);
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    }

    getCategories();
  }, []);

  console.log(categories);
  /*
  const expenses = categories.expense.map(expense => {
    const { name, ...expenseValues } = expense;
    expenseValues.label = name;

    return expenseValues;
  });

  const earnings = categories.earning.map(earning => {
    const { name, ...earningValues } = earning;
    earningValues.label = name;

    return earningValues;
  });
*/
  //return [expenses, earnings];
};

export default useCategories;
