import React from 'react';
import PropTypes from 'prop-types';
import { useField, useFormikContext } from 'formik';
import Select, { components } from 'react-select';
import 'react-datepicker/dist/react-datepicker.css';
import { StyledLabel, ErrorMessage } from '../styles';
import StyledOption from './styles';

const customStyles = {
  control: styles => ({
    ...styles,
    backgroundColor: 'white',
    borderRadius: '5px',
    border: '1px solid #c1c1c1',
    width: '100%',
    height: '40px',
    background: '#fff',
    fontSize: '15px',
  }),

  option: (props) => {
    console.log(props);
      return {
        ...styles,
        backgroundColor: isSelected 
        ? '#ffedec' 
        : isFocused
        ? '#B2D4FF'
        : null,
        color: '#000'
      };
  }
};

const Option = props => {
  const { data } = props;

  return (
    <StyledOption style={{ display: 'flex', alignItems: 'center' }}>
      {data.icon && <i className="input-select__icon">{data.icon}</i>}
      <components.Option {...props} />
    </StyledOption>
  );
};

const MySelect = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  const { setFieldValue } = useFormikContext();
  const { id, placeholder, options } = props;
  const { name, value, onBlur } = field;

  return (
    <>
      <StyledLabel htmlFor={id || name}>{label}</StyledLabel>
      <Select
        id={id || name}
        styles={customStyles}
        onChange={category => setFieldValue('category', category)}
        name={name}
        value={value}
        onBlur={onBlur}
        placeholder={placeholder}
        options={options}
        components={{ Option }}
      />
      {meta.touched && meta.error ? (
        <ErrorMessage>{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

MySelect.defaultProps = {
  label: '',
  placeholder: '',
  id: null,
};

MySelect.propTypes = {
  options: PropTypes.arrayOf(PropTypes.object).isRequired,
  label: PropTypes.string,
  id: PropTypes.string,
  placeholder: PropTypes.string,
};

Option.propTypes = {
  data: PropTypes.objectOf(PropTypes.node).isRequired,
};
export default MySelect;
