import React from 'react';
import createNumberMask from 'text-mask-addons/dist/createNumberMask';
import { Formik } from 'formik';
import MySelect from '../FormComponents/MySelect';
import MyMaskedInput from '../FormComponents/MyMaskedInput';
import { InputItem, InputGroup } from '../FormComponents/styles';
import { Button } from '../Button';
import useCategories from '../../hooks/useCategories';

const initialValues = {
  category: '',
  amount: '',
};

const numberMask = createNumberMask({
  allowDecimal: true,
  prefix: '',
  thousandsSeparatorSymbol: '.',
  decimalSymbol: ',',
});

const AddBudgets = () => {
  const [expensesCategories] = useCategories();

  return (
    <Formik initialValues={initialValues}>
      <InputGroup>
        <InputItem>
          <MySelect
            options={expensesCategories}
            name="category"
            label="Category"
            placeholder="Select..."
          />
        </InputItem>
        <InputItem>
          <MyMaskedInput
            mask={numberMask}
            label="Amount"
            name="amount"
            placeholder="0.00"
          />
        </InputItem>
      </InputGroup>
      <Button type="submit">Submit</Button>
    </Formik>
  );
};

export default AddBudgets;
