import styled from 'styled-components';

const StyledInput = styled.input`
  padding: 5px;
  border-radius: 5px;
  border: 1px solid #c1c1c1;
`;

const StyledLabel = styled.label`
  display: block;
  margin-bottom: 3px;
`;

const FormSection = styled.div``;

export { StyledInput, StyledLabel, FormSection };
