import React from 'react';
import { useField, useFormikContext } from 'formik';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import {
  StyledInput,
  StyledLabel,
  StyledDatePickerWrapper,
  ErrorMessage,
  StyledRadio,
  StyledTextArea,
  StyledCheckBox,
  StyledMaskedInput,
} from './styles';

const MyMaskedInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <StyledMaskedInput {...field} {...props} type="text" />
      {meta.touched && meta.error ? (
        <ErrorMessage>{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

const MySelect = ({ label, children, ...props }) => {
  const [field, meta] = useField(props);

  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <select {...field} {...props}>
        {children}
      </select>
      {meta.touched && meta.error ? (
        <ErrorMessage>{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

const MyTextInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <StyledInput {...field} {...props} />
      {meta.touched && meta.error ? (
        <ErrorMessage>{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

const MyTextArea = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <StyledTextArea rows="4" {...field} {...props} />
      {meta.touched && meta.error ? (
        <ErrorMessage>{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

const MyDateField = ({ label, ...props }) => {
  const [field, meta] = useField({ ...props, type: 'date' });

  const { setFieldValue } = useFormikContext();
  const { onChange, ...newField } = field;

  return (
    <>
      <StyledLabel htmlFor={props.id || props.name}>{label}</StyledLabel>
      <StyledDatePickerWrapper>
        <DatePicker
          selected={meta.value}
          onChange={date => setFieldValue('date', date)}
          {...newField}
          {...props}
        />
      </StyledDatePickerWrapper>
      {meta.touched && meta.error ? (
        <ErrorMessage className="error">{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

const MyCheckbox = ({ children, ...props }) => {
  const [field, meta] = useField({ ...props, type: 'checkbox' });
  return (
    <>
      <StyledCheckBox>
        <input {...field} {...props} type="checkbox" />
        {children}
      </StyledCheckBox>
      {meta.touched && meta.error ? (
        <ErrorMessage className="error">{meta.error}</ErrorMessage>
      ) : null}
    </>
  );
};

const MyTransactionTypeButton = ({ children, ...props }) => {
  const [field, meta] = useField({ ...props, type: 'radio' });
  return (
    <StyledRadio>
      <label>
        <input {...field} {...props} type="radio" />
        {children}
      </label>
      {meta.touched && meta.error ? (
        <ErrorMessage className="error">{meta.error}</ErrorMessage>
      ) : null}
    </StyledRadio>
  );
};

export {
  MyCheckbox,
  MyDateField,
  MyTextInput,
  MyTransactionTypeButton,
  MyTextArea,
  MyMaskedInput,
  MySelect,
};
