import React from 'react';
import PropType from 'prop-types';
import Pie from '../Graphs/Pie';
import useCategories from '../../hooks/useCategories';

// fazer uma array do tipo [{budget, expense}, ....]
// criar um use expenses
// map nos budgets e filtra as expenses.
// ver como fazer o sort do data do pie na ordem de entrada.

const BudgetList = ({ data }) => {
  const [expenses] = useCategories();

  const teste = data.map(budget => {
    const [budgetAndExpense] = expenses.filter(
      expense => expense.value === budget.id
    );

    return budgetAndExpense;
  });

  console.log(teste);

  const colors = ['#ff8300', '#ccc'];
  return (
    <div>
      <Pie
        data={data}
        width={50}
        height={50}
        innerRadius={15}
        outerRadius={25}
        colors={colors}
      />
    </div>
  );
};

BudgetList.defaultProps = {
  data: {},
};

BudgetList.propTypes = {
  data: PropType.arrayOf(PropType.object),
};

export default BudgetList;
