import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const ReportGraph = (width, height) => {
  const graphRef = useRef(null);

  const dataset = [
    { name: 'bar', value: 300, type: 'expense' },
    { name: 'shopping', value: 400, type: 'earning' },
    { name: 'party', value: 200, type: 'earning' },
  ];

  console.log(width);

  useEffect(() => {
    const g = d3.select(graphRef.current);

    const yValue = d => d.value;
    const xValue = d => d.name;

    const yScale = d3
      .scaleLinear()
      .domain([0, d3.max(dataset, yValue)])
      .range([0, 500]);

    const xScale = d3
      .scaleBand()
      .domain(dataset.map(xValue))
      .range([0, 500])
      .padding(0.1);

    g.append('g').call(d3.axisLeft(xScale));
    g.append('g')
      .call(d3.axisBottom(yScale))
      .attr('transform', `translate(0, ${500})`);

    //g.attr('transform', `translate(${margin.left}, ${margin.top})`);

    g.selectAll('rect')
      .data(dataset)
      .enter()
      .append('rect')
      .attr('x', d => xScale(xValue(d)))
      .attr('height', d => yScale(yValue(d)))
      .attr('width', xScale.bandwidth());
  });

  return (
    <svg height={height} width={width}>
      <g ref={graphRef} />
    </svg>
  );
};

export default ReportGraph;
