import React, { useRef } from 'react';
import 'tippy.js/dist/tippy.css';
import * as d3 from 'd3';
import tippy, { followCursor } from 'tippy.js';
import {
  LegendColorBox,
  LegendTitle,
  LegendPercentage,
  LegendItem,
  PieWrapper,
} from './styles';

const Pie = ({
  data,
  outerRadius,
  innerRadius = 0,
  width,
  height,
  legend,
  colors,
}) => {
  const graphRef = useRef(null);

  const graphContainer = d3.select(graphRef.current);

  const arcs = d3
    .pie()
    .value(value => value.value)(data)
    .sort((a, b) => a.data.name.localeCompare(b.data.name));

  const arc = d3
    .arc()
    .innerRadius(innerRadius)
    .outerRadius(outerRadius);

  graphContainer
    .selectAll('path')
    .data(arcs)
    .enter()
    .append('path')
    .attr('fill', d => {
      return colors[d.index];
    })
    .attr('stroke', 'white')
    .attr('d', arc)
    .on('mouseover', d => {
      tippy(d3.event.target, {
        content: d.data.name,
        theme: 'light',
        arrow: false,
        followCursor: true,
        plugins: [followCursor],
      });
    })
    .call(d => {
      console.log(d3.local());
    });

  return (
    <PieWrapper>
      <div>
        <svg width={width} height={height}>
          <g
            ref={graphRef}
            transform={`translate(${outerRadius} ${outerRadius})`}
          />
        </svg>
      </div>
      {legend && (
        <ul>
          {arcs.map(arc => (
            <LegendItem key={arc.data.id}>
              <LegendColorBox color={colors[arc.index]} />
              <LegendTitle>{arc.data.name}: </LegendTitle>
              <LegendPercentage>{arc.data.value}</LegendPercentage>
            </LegendItem>
          ))}
        </ul>
      )}
    </PieWrapper>
  );
};

export default Pie;
