import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const ReportGraph = () => {
  const graphRef = useRef(null);

  const dataset = [
    { name: 'bar', value: 300, type: 'expense' },
    { name: 'shopping', value: 400, type: 'earning' },
  ];

  const xScale = d3.scaleLinear().domain([0, max(dataset, d => d.value)]);

  useEffect(() => {
    const g = d3.select(graphRef.current);

    g.selectAll('rect')
      .data(dataset)
      .enter()
      .append('rect')
      .attr('width', '300')
      .attr('height', '300');
  });

  return (
    <svg ref={graphRef} height="500" width="500">
      <g ref={graphRef} />
    </svg>
  );
};

export default ReportGraph;
