import React from 'react';
import PropType from 'prop-types';
import Pie from '../Graphs/Pie';
import useTransactions from '../../hooks/useTransactions';
import { BudgetDetailsWrapper } from './styles';

// fornecer a opção de dar ou não o sort
// finalizar e revisar este componente e o useTransactionse o Pie

const BudgetList = ({ data }) => {
  const [transactions, loading] = useTransactions();
  const budgets = [];

  data.forEach(budget => {
    const [budgetAndExpense] = transactions.filter(
      transaction => transaction.category === budget.id
    );

    if (budgetAndExpense) {
      budgets.push([
        {
          name: budgetAndExpense.title,
          value: parseFloat(budgetAndExpense.amount),
          id: budgetAndExpense.id,
        },
        {
          ...budget,
          value: budget.value - parseFloat(budgetAndExpense.amount),
        },
      ]);
    }
  });

  const colors = ['#ff8300', '#ccc'];
  return loading ? (
    <p>teste</p>
  ) : (
    budgets.map(budget => (
      <div key={budget[1].id}>
        <Pie
          data={budget}
          width={50}
          height={50}
          innerRadius={15}
          outerRadius={25}
          colors={colors}
        />
        <div>
          <p>{budget[1].name}</p>
          <span>Budget: R$ {budget[1].value}</span>
          <span>Spent: R$ {budget[0].value}</span>
        </div>
      </div>
    ))
  );
};

BudgetList.defaultProps = {
  data: {},
};

BudgetList.propTypes = {
  data: PropType.arrayOf(PropType.object),
};

export default BudgetList;
