import styled from 'styled-components';

const ValueText = styled.span`'
  color: ${props => {
    if (props.green) {
      return '#00b300';
    } else if (props.red) {
      return '#e60000';
    } else {
      return '#e2e2e2';
    }
  }};
  font-weight: bold;
  font-size: 30px;
`;

export { ValueText };
