import React, { useRef } from 'react';
import * as d3 from 'd3';
import * as popper from 'ṕopper.js';
import {
  LegendColorBox,
  LegendTitle,
  LegendPercentage,
  LegendItem,
  PieWrapper,
} from './styles';

const Pie = ({
  data,
  outerRadius,
  innerRadius = 0,
  width,
  height,
  legend,
  colors,
}) => {
  const graphRef = useRef(null);
  const graphWrapperRef = useRef(null);

  const graphContainer = d3.select(graphRef.current);
  const graphWrapper = d3.select(graphWrapperRef.current);

  const arcs = d3
    .pie()
    .value(value => value.value)(data)
    .sort((a, b) => a.data.name.localeCompare(b.data.name));

  const arc = d3
    .arc()
    .innerRadius(innerRadius)
    .outerRadius(outerRadius);

  console.log(popper);
  graphContainer
    .selectAll('path')
    .data(arcs)
    .enter()
    .append('path')
    .attr('fill', d => {
      return colors[d.index];
    })
    .attr('stroke', 'white')
    .attr('d', arc)
    .on('mouseover', (data, index, references) => {
      graphWrapper.style('opacity', 0.9);
      //popper.reference = references[index];
      popper.update();
    });

  graphWrapper.append('div').attr('class', 'tooltip');

  return (
    <PieWrapper>
      <div ref={graphWrapperRef}>
        <svg width={width} height={height}>
          <g
            ref={graphRef}
            transform={`translate(${outerRadius} ${outerRadius})`}
          />
        </svg>
      </div>
      {legend && (
        <ul>
          {arcs.map(arc => (
            <LegendItem key={arc.data.id}>
              <LegendColorBox color={colors[arc.index]} />
              <LegendTitle>{arc.data.name}: </LegendTitle>
              <LegendPercentage>{arc.data.value}</LegendPercentage>
            </LegendItem>
          ))}
        </ul>
      )}
    </PieWrapper>
  );
};

export default Pie;
