import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const ReportGraph = () => {
  const graphRef = useRef(null);

  const dataset = [
    { name: 'bar', value: 300, type: 'expense' },
    { name: 'shopping', value: 400, type: 'earning' },
  ];

  useEffect(() => {
    const g = d3.select(graphRef.current);
    const margin = { top: 20, right: 20, bottom: 20, left: 20 };
    const innerWidth = 500 - margin.left - margin.right;
    const innerHeight = 500 - margin.top - margin.bottom;

    const xValue = d => d.value;
    const yValue = d => d.name;

    const xScale = d3
      .scaleLinear()
      .domain([0, d3.max(dataset, xValue)])
      .range([0, innerWidth]);

    const yAxis = d3.axisLeft(yScale);

    const yScale = d3
      .scaleBand()
      .domain(dataset.map(yValue))
      .range([0, innerHeight]);

    yAxis(g.append('g'));

    g.attr('transform', `translate(${margin.left}, ${margin.top})`);

    g.selectAll('rect')
      .data(dataset)
      .enter()
      .append('rect')
      .attr('y', d => yScale(yValue(d)))
      .attr('width', d => xScale(xValue(d)))
      .attr('height', yScale.bandwidth());
  });

  return (
    <svg height="500" width="500">
      <g ref={graphRef} />
    </svg>
  );
};

export default ReportGraph;
