export default transactions => {
  const date = new Date();
  let income = 0;
  let expenses = 0;
  let predictedIncome = 0;
  let predictedExpenses = 0;

  // vou filtrar por pago ou n e add, depois fazer o post
  transactions.forEach(transaction => {
    if (transaction.type === 'expense') {
      if (transaction.paid) {
        expenses += parseFloat(
          transaction.amount.replace('.', '').replace(',', '.')
        );
      } else {
        predictedExpenses += transaction.amount;
      }
    } else if (transaction.type === 'income') {
      if (transaction.paid) {
        income += transaction.amount;
      } else {
        predictedIncome += transaction.amount;
      }
    }
  });

  console.log(expenses);
  console.log(predictedExpenses);
};
