import totals from '../../backend/totals';

const Home = () => {
  const [transactions, loading] = useTransactions([]);
  //const [balance, income, expenses] = totals(transactions);
  return (
    <>
      <PageTitle>Home</PageTitle>
      <FlexRow>
        <ShowValue color="#00b300" value={balance} title="Balance" />
        <ShowValue color="#00b300" value={income} title="Income" />
        <ShowValue color="#e00000" value={expenses} title="Outcome" />
      </FlexRow>
    </>
  );
};

export default Home;
