import React from 'react';
import { Formik } from 'formik';
import MySelect from '../FormComponents/MySelect';
import MyMaskedInput from '../FormComponents/MyMaskedInput';
import { InputItem } from '../FormComponents/styles';
import useCategories from '../../hooks/useCategories';

const initialValues = {
  category: '',
  amount: '',
};

const AddBudgets = () => {
  const [expensesCategories] = useCategories();

  return (
    <Formik initialValues={initialValues}>
      <InputGroup>
        <InputItem>
          <MySelect
            options={expensesCategories}
            name="category"
            label="Category"
            placeholder="Select..."
          />
        </InputItem>
        <InputItem>
          <MyMaskedInput />
        </InputItem>
      </InputGroup>
    </Formik>
  );
};

export default AddBudgets;
