import React, { useRef } from 'react';
import * as d3 from 'd3';

const ReportGraph = () => {
  const graphRef = useRef(null);

  const dataset = [
    { name: 'bar', value: 300, type: 'expense' },
    { name: 'shopping', value: 400, type: 'earning' },
  ];

  const svg = d3.select(graphRef.current);

  svg
    .selectAll('rect')
    .data(dataset)
    .enter()
    .append('rect')
    .attr('width', 300)
    .attr('height', 300);

  return <svg ref={graphRef} height="500" width="500"></svg>;
};

export default ReportGraph;
