import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaExchangeAlt, FaHome } from 'react-icons/fa';

const StyledSideBar = styled.aside`
    width: 300px;
    padding: 50px 10px;
`;

const StyledExangeIcon = styled(FaExchangeAlt)`
    margin-left: 15px;
`;

const StyledLink = styled(Link)`
    color: #696969;
    font-weight: bold;
    text-decoration: none;
    display: flex;
    margin-bottom: 15px;

    ${StyledExangeIcon} {
        transform: rotate(180deg);
        transition: 300ms;
    }

    &:hover ${StyledExangeIcon} {
        transform: rotate(540deg);
        transition: 300ms;
    }
`;

export { StyledSideBar, StyledLink, StyledExangeIcon };
