import React from 'react';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import { MyCheckbox, MyDateField, MyTextInput } from '../FormComponents';

const AddTransaction = () => {
  return (
    <Formik
      initialValues={{
        amount: '',
        description: '',
        date: new Date(),
        paid: false,
      }}
      validationSchema={Yup.object({
        description: Yup.string()
          .max(15, 'Must be 60 characters or less')
          .required('Required'),
        amount: Yup.number().required('Required'),
        date: Yup.date().required('Required'),
        acceptedTerms: Yup.boolean()
          .required('Required')
          .oneOf([true], 'You must accept the terms and conditions.'),
        jobType: Yup.string()
          // specify the set of valid values for job type
          // @see http://bit.ly/yup-mixed-oneOf
          .oneOf(
            ['designer', 'development', 'product', 'other'],
            'Invalid Job Type'
          )
          .required('Required'),
      })}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      <Form>
        <MyTextInput label="Description" name="description" type="text" />
        <MyTextInput
          label="Amount"
          name="amount"
          type="text"
          placeholder="0,00"
        />
        <MyDateField label="Date" name="date" />
        <MyCheckbox name="paid">Paid</MyCheckbox>
      </Form>
    </Formik>
  );
};

export default AddTransaction;
