import styled from 'styled-components';

const StyledIcon = styled.i`
  background: #1fd279;
  color: #fff;
  border-radius: 50%;
  display: inlin-flex;
  align-items: center;
  justify-content: center;
  padding: 10px;
  text-align: center;
  font-size: 20px;
`;

const StyledOption = styled.div`
  display: flex;
  align-items: center;
`;

export default StyledIcon;
