import { useRef } from 'react';
import * as d3 from 'd3';

const Pie = () => {
  const ref = useRef(null);

  const data = [
    { name: 'Bar & restaurants', value: 100, id: '1' },
    { name: 'Clothing', value: 300, id: '2' },
    { name: 'Debts & loans', value: 100, id: '3' },
    { name: 'Education', value: 1000, id: '4' },
    { name: 'Entertainment & hobbies', value: 500, id: '5' },
    { name: 'Food', value: 500, id: '7' },
    { name: 'Health', value: 200, id: '9' },
    { name: 'Others', value: 500, id: '12' },
    { name: 'Subscriptions & services', value: 50, id: '16' },
  ];

  const arcs = d3
    .pie()
    .value(value => value.value)(data)
    .sort((a, b) => a.data.name.localeCompare(b.data.name));

  console.log(arcs);
};

export default Pie;
