import React, { useContext } from 'react';
import uuidv4 from 'uuid';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import { FaPlus, FaMinus } from 'react-icons/fa';
import api from '../../services/api';
import {
  MyCheckbox,
  MyDateField,
  MyTextInput,
  MyTransactionTypeButton,
  MyTextArea,
} from '../FormComponents';
import {
  InputItem,
  InputGroup,
  InputItemCenter,
} from '../FormComponents/styles';
import { StyledFaMinus, StyledFaPlus, StyledCenteredButton } from './styles';
import { TransactionsContext } from '../../contexts/TransactionContext';

const AddTransaction = ({ modalClose }) => {
  const [transactions, setTransactions] = useContext(TransactionsContext);

  return (
    <Formik
      initialValues={{
        amount: '',
        title: '',
        description: '',
        date: new Date(),
        paid: false,
        type: 'expense',
      }}
      validationSchema={Yup.object({
        title: Yup.string()
          .max(15, 'Must be 60 characters or less')
          .required('Required'),
        amount: Yup.number().required('Required'),
        date: Yup.date().required('Required'),
        description: Yup.string()
          .max(15, 'Must be 150 characters or less')
          .required('Required'),
        paid: Yup.boolean().required('Required'),
        type: Yup.string()
          .oneOf(['income', 'expense'])
          .required('Required'),
      })}
      onSubmit={values => {
        console.log({ ...values, id: uuidv4() });
        api.post('/transactions', { ...values });
        setTransactions([...transactions, values]);
        modalClose();
      }}
    >
      <Form>
        <InputItemCenter>
          <MyTransactionTypeButton name="type" value="expense">
            <StyledFaMinus>
              <FaMinus />
            </StyledFaMinus>
            Expense
          </MyTransactionTypeButton>
          <MyTransactionTypeButton name="type" value="income">
            <StyledFaPlus>
              <FaPlus />
            </StyledFaPlus>
            Income
          </MyTransactionTypeButton>
        </InputItemCenter>
        <InputItem>
          <MyTextInput label="Title" name="title" type="text" />
        </InputItem>
        <InputGroup>
          <InputItem>
            <MyTextInput
              label="Amount"
              name="amount"
              type="text"
              placeholder="0,00"
            />
          </InputItem>
          <InputItem>
            <MyDateField label="Date" name="date" />
          </InputItem>
        </InputGroup>

        <InputItem>
          <MyTextArea label="Description" name="description" type="textarea" />
        </InputItem>
        <MyCheckbox name="paid">Paid</MyCheckbox>
        <StyledCenteredButton type="submit">Submit</StyledCenteredButton>
      </Form>
    </Formik>
  );
};

export default AddTransaction;
