import React, { useRef } from 'react';
import * as d3 from 'd3';

const reportGraph = () => {
  const graphRef = useRef(null);

  const dataset = [
    { name: 'bar', value: 300, type: 'expense' },
    { name: 'shopping', value: 400, type: 'earning' },
  ];

  const g = d3.select(graphRef.current);

  const height = 200;
  const width = 100;

  dataset.then(function(data) {
    g.selectAll('div')
      .data(data)
      .enter()
      .append('rect')
      .attr('x', function(data, i) {
        for (i > 0; i < data.length; i++) {
          return i * 21;
        }
      })
      .attr('y', function(d) {
        return height - d.val * 10;
      })
      .attr('width', 20)
      .attr('height', function(d) {
        return d.val * 10;
      });
  });

  return (
    <svg>
      <g ref={graphRef} />
    </svg>
  );
};

export default reportGraph;
