import React from 'react';
import PropType from 'prop-types';
import Pie from '../Graphs/Pie';
import useTransactions from '../../hooks/useTransactions';

// fazer uma array do tipo [{budget, expense}, ....]
// criar um use expenses
// map nos budgets e filtra as expenses.
// ver como fazer o sort do data do pie na ordem de entrada.
// when a budget is created I have to run useTransactions again

const BudgetList = ({ data }) => {
  const [transactions, loading] = useTransactions();
  const budgets = [];

  data.forEach(budget => {
    const [budgetAndExpense] = transactions.filter(
      transaction => transaction.category === budget.id
    );

    if (budgetAndExpense) {
      teste.push({ ...budget, amount: budgetAndExpense.amount });
    }
  });

  const colors = ['#ff8300', '#ccc'];
  return (
    <div>
      <Pie
        data={data}
        width={50}
        height={50}
        innerRadius={15}
        outerRadius={25}
        colors={colors}
      />
    </div>
  );
};

BudgetList.defaultProps = {
  data: {},
};

BudgetList.propTypes = {
  data: PropType.arrayOf(PropType.object),
};

export default BudgetList;
