import React from 'react';
import FlexRow from '../../containers/FlexRow';
import ShowValue from '../../components/ShowValue';
import PageTitle from '../../components/PageTitle';
import useTransactions from '../../hooks/useTransactions';
import totals from '../../backend/totals';

const Home = () => {
  const [transactions, loading] = useTransactions([]);
  const [balance, income, expenses] = totals(transactions);

  console.log(totals(transactions));

  return (
    <>
      <PageTitle>Home</PageTitle>
      <FlexRow>
        <ShowValue color="#00b300" value=",09990" title="Balance" />
        <ShowValue color="#00b300" value="999,00" title="Income" />
        <ShowValue color="#e00000" value="999,00" title="Outcome" />
      </FlexRow>
    </>
  );
};

export default Home;
