import React, { useRef } from 'react';
import * as d3 from 'd3';
import {
  LegendColorBox,
  LegendTitle,
  LegendPercentage,
  LegendItem,
} from './styles';

const Pie = ({
  data,
  outerRadius,
  innerRadius = 0,
  width,
  height,
  legend,
  colors,
}) => {
  const graphRef = useRef(null);

  const graphContainer = d3.select(graphRef.current);

  const arcs = d3
    .pie()
    .value(value => value.value)(data)
    .sort((a, b) => a.data.name.localeCompare(b.data.name));

  const arc = d3
    .arc()
    .innerRadius(innerRadius)
    .outerRadius(outerRadius);

  graphContainer
    .selectAll('path')
    .data(arcs)
    .enter()
    .append('path')
    .attr('fill', d => colors[d.index])
    .attr('stroke', 'white')
    .attr('d', arc);

  return (
    <>
      <div>
        <svg width={width} height={height}>
          <g
            ref={graphRef}
            transform={`translate(${outerRadius} ${outerRadius})`}
          />
        </svg>
      </div>
      {legend && (
        <ul>
          {arcs.map(arc => (
            <LegendItem>
              <LegendColorBox color={colors[arc.index]} />
              <LegendTitle>{arc.data.name}: </LegendTitle>
              <LegendPercentage>{arc.data.value}</LegendPercentage>
            </LegendItem>
          ))}
        </ul>
      )}
    </>
  );
};

export default Pie;
