import api from '../services/api';

const useCategories = () => {
  async function getCategories() {
    try {
      const response = await api.get('/categories');
      return response.data;
    } catch (error) {
      console.error(error);
      return [];
    }
  }

  const categories = getCategories();
};

export default useCategories;
