import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';

const ReportGraph = () => {
  const graphRef = useRef(null);

  const dataset = [
    { name: 'bar', value: 300, type: 'expense' },
    { name: 'shopping', value: 400, type: 'earning' },
  ];

  const svg = d3.select(graphRef.current);

  useEffect(() => {
    svg
      .selectAll('rect')
      .data(dataset, d => {
        console.log(d);
        return d.name;
      })
      .enter()
      .append('rect')
      .attr('width', '300')
      .attr('height', '300');
  }, []);

  return <svg ref={graphRef} height="500" width="500" />;
};

export default ReportGraph;
