import React, { useContext, useFormikContext } from 'react';
import uuidv4 from 'uuid';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import createNumberMask from 'text-mask-addons/dist/createNumberMask';
import { FaPlus, FaMinus } from 'react-icons/fa';
import api from '../../services/api';
import {
  MyCheckbox,
  MyDateField,
  MyTextInput,
  MyTransactionTypeButton,
  MyTextArea,
  MyMaskedInput,
  MySelect,
} from '../FormComponents';
import {
  InputItem,
  InputGroup,
  InputItemCenter,
} from '../FormComponents/styles';
import { StyledFaMinus, StyledFaPlus, StyledCenteredButton } from './styles';
import { TransactionsContext } from '../../contexts/TransactionContext';
import useCategories from '../../hooks/useCategories';

const AddTransaction = ({ modalClose }) => {
  const [transactions, setTransactions] = useContext(TransactionsContext);

  const [expensesCategories, earningsCategories] = useCategories([]);

  const initialFormValues = {
    amount: '',
    title: '',
    description: '',
    date: new Date(),
    paid: false,
    type: 'expense',
    category: 'Other Earnings',
  };

  const addTransactionSchema = Yup.object({
    title: Yup.string()
      .max(15, 'Must be 60 characters or less')
      .required('Required'),
    amount: Yup.string().required('Required'),
    date: Yup.date().required('Required'),
    description: Yup.string()
      .max(15, 'Must be 500 characters or less')
      .required('Required'),
    paid: Yup.boolean().required('Required'),
    type: Yup.string()
      .oneOf(['income', 'expense'])
      .required('Required'),
  });

  const handleSubmit = (values, { resetForm }) => {
    const newTransaction = { ...values, id: uuidv4() };

    api.post('/transactions', newTransaction);
    setTransactions([...transactions, newTransaction]);
    modalClose();
    resetForm();
  };

  const numberMask = createNumberMask({
    allowDecimal: true,
    prefix: '',
    thousandsSeparatorSymbol: '.',
    decimalSymbol: ',',
  });

  return (
    <Formik
      initialValues={initialFormValues}
      validationSchema={addTransactionSchema}
      onSubmit={handleSubmit}
    >
      <Form>
        <InputItemCenter>
          <MyTransactionTypeButton name="type" value="expense">
            <StyledFaMinus>
              <FaMinus />
            </StyledFaMinus>
            Expense
          </MyTransactionTypeButton>
          <MyTransactionTypeButton name="type" value="income">
            <StyledFaPlus>
              <FaPlus />
            </StyledFaPlus>
            Income
          </MyTransactionTypeButton>
        </InputItemCenter>

        <InputGroup>
          <InputItem>
            <MyTextInput label="Title" name="title" type="text" />
          </InputItem>
          <InputItem>
            {formik.values ? (
              <MySelect label="Category" name="categories" />
            ) : (
              <MySelect label="Category" name="categories" />
            )}
          </InputItem>
        </InputGroup>

        <InputGroup>
          <InputItem>
            <MyMaskedInput
              mask={numberMask}
              label="Amount"
              name="amount"
              placeholder="0.00"
            />
          </InputItem>
          <InputItem>
            <MyDateField label="Date" name="date" />
          </InputItem>
        </InputGroup>

        <InputItem>
          <MyTextArea label="Description" name="description" type="textarea" />
        </InputItem>
        <MyCheckbox name="paid">Paid</MyCheckbox>
        <StyledCenteredButton type="submit">Submit</StyledCenteredButton>
      </Form>
    </Formik>
  );
};

export default AddTransaction;
