import React, { useRef } from 'react';
import * as d3 from 'd3';
import {
  LegendColorBox,
  LegendTitle,
  LegendPercentage,
  LegendItem,
  PieWrapper,
} from './styles';

const Pie = ({
  data,
  outerRadius,
  innerRadius = 0,
  width,
  height,
  legend,
  colors,
}) => {
  const graphRef = useRef(null);
  const tooltipRef = useRef(null);

  const graphContainer = d3.select(graphRef.current);
  const tooltip = d3
    .select(tooltipRef.current)
    .style('position', 'absolute')
    .style('z-index', '10')
    .style('visibility', 'hidden')
    .text('a simple tooltip');

  const arcs = d3
    .pie()
    .value(value => value.value)(data)
    .sort((a, b) => a.data.name.localeCompare(b.data.name));

  const arc = d3
    .arc()
    .innerRadius(innerRadius)
    .outerRadius(outerRadius);

  graphContainer
    .selectAll('path')
    .data(arcs)
    .enter()
    .append('path')
    .attr('fill', d => {
      return colors[d.index];
    })
    .attr('stroke', 'white')
    .attr('d', arc)
    .on('mouseover', function(event) {
      return tooltip.style('visibility', 'visible');
    })
    .on('mousemove', function(event) {
      console.log(d3.event);
      return tooltip
        .style('top', d3.event.pageY + 'px')
        .style('left', d3.event.pageX + 'px');
    })
    .on('mouseout', function() {
      return tooltip.style('visibility', 'hidden');
    });

  return (
    <PieWrapper>
      <div>
        <svg width={width} height={height}>
          <g
            ref={graphRef}
            transform={`translate(${outerRadius} ${outerRadius})`}
          />
        </svg>
        <div ref={tooltipRef}>teste</div>
      </div>
      {legend && (
        <ul>
          {arcs.map(arc => (
            <LegendItem key={arc.data.id}>
              <LegendColorBox color={colors[arc.index]} />
              <LegendTitle>{arc.data.name}: </LegendTitle>
              <LegendPercentage>{arc.data.value}</LegendPercentage>
            </LegendItem>
          ))}
        </ul>
      )}
    </PieWrapper>
  );
};

export default Pie;
