import styled from 'styled-components';

const ValueText = styled.span`'
  color: ${props => {
    if (props.green) {
      ('#00b300');
    } else if (props.red) {
      ('#e60000');
    }
  }};
  font-weight: bold;
  font-size: 30px;
`;

export { ValueText };
