import styled from 'styled-components';

const BudgetWrapper = styled.div`
  display: flex;
  align-items: center;
`;

const BudgetDetailsWrapper = styled.div`
  margin-left: 15px;
`;

const BudgetDetailsTitle = styled.h3`
  font-weight: bold;
`;

export { BudgetWrapper, BudgetDetailsWrapper, BudgetDetailsTitle };
