import { useState, useEffect } from 'react';
import api from '../services/api';

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    async function getCategories() {
      try {
        const response = await api.get('/categories');
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    }

    getCategories();
  }, []);

  categories.expense.map;
  /*
  const expenses = categories
    .filter(category => category.type === 'expense')
    .map(expense => {
      const { name, ...expenseValues } = expense;
      expenseValues.label = name;

      return expenseValues;
    });

  const earnings = categories
    .filter(category => category.type === 'earning')
    .map(earning => {
      const { name, ...earningValues } = earning;
      earningValues.label = name;

      return earningValues;
    });
    */

  return [[], []];
};

export default useCategories;
