import api from '../services/api';

const useCategories = () => {
  let categories;

  async function getCategories() {
    try {
      const response = await api.get('/categories');
      categories = response.data;
    } catch (error) {
      console.error(error);
      return [];
    }
  }

  getCategories();

  return categories;
};

export default useCategories;
