import React, { useState, useEffect } from 'react';
import { FaCheck, FaTimes } from 'react-icons/fa';
import PageTitle from '../../components/PageTitle';
import Card from '../../containers/Card';
import api from '../../services/api';
import {
  TransactionItem,
  TransactionStatus,
  TransactionTitle,
  TransactionValue,
  TransactionDate,
  ButtonAddTransaction,
} from './styles';
import Modal from '../../containers/Modal';

const MyTextInput = ({ label, ...props }) => {
  // useField() returns [formik.getFieldProps(), formik.getFieldMeta()]
  // which we can spread on <input> and alse replace ErrorMessage entirely.
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input className="text-input" {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ) : null}
    </>
  );
};

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    async function getTransactions() {
      try {
        const response = await api.get('/transactions');
        setTransactions(response.data);
      } catch (error) {
        console.error(error);
      }
    }

    getTransactions();
  }, []);

  const handleClose = e => {
    if (e.target === e.currentTarget) {
      setModalOpen(false);
    }
  };

  return (
    <>
      <PageTitle>Transactions</PageTitle>
      <Card backgroundColor="#fff" borderRadius="10px">
        <ul>
          {transactions.map(transaction => {
            return (
              <TransactionItem key={transaction.id}>
                <TransactionDate>{transaction.date}</TransactionDate>
                <TransactionTitle>{transaction.title}</TransactionTitle>
                <TransactionStatus status={transaction.paid}>
                  {transaction.paid ? <FaCheck /> : <FaTimes />}
                </TransactionStatus>
                <TransactionValue>{transaction.value}</TransactionValue>
              </TransactionItem>
            );
          })}
        </ul>
      </Card>
      <ButtonAddTransaction onClick={() => setModalOpen(true)}>
        Add Transaction
      </ButtonAddTransaction>
      <Modal open={modalOpen} onClose={handleClose}></Modal>
    </>
  );
};

export default Transactions;
